// Create user
var newUser = {
  name: faker.name.firstName(),
  email: faker.internet.email(),
  password: faker.internet.password()
};
//register a new user first then authenticate it to continue
request.post('/api/v1/users').send(newUser).expect(200).end(function (err, res) {
  if (err) return done(err);
  // Authenticate user
  var credentials = {
    email: newUser.email,
    password: newUser.password
  };
  request.post('/api/v1/auth').send(credentials).expect(200).end(function (err, res) {
    if (err) return done(err);
    authToken = res.body.token; //to authenticate user to get token used to delete user by token
    done();
  });
});

